mapper.update:
update sku_list
set price = #{price}, weight = #{weight}, skuName = #{skuName}, skuDesc = #{skuDesc},
    skuDefaultImage = #{skuDefaultImage}
where id = #{id}

mapper.select:
select distinct s.*
from sku_list s left outer join sku_attr_value sa on (s.id = sa.skuId)
where category3Id = #{category3Id} and isSale = 1

  and tmId = #{tmId}


  and sa.attrValueId in

        #{item.attrValueId}

  group by s.id
  having count(*) = #{skuAttrValueNum}


    order by ${orderOf} asc


    order by ${orderOf} desc

limit #{index}, #{size}

select.if:
select count(*)
from (select count(distinct s.id)
      from sku_list s left outer join sku_attr_value sa on (s.id = sa.skuId)
      where category3Id = #{category3Id} and isSale = 1

        and tmId = #{tmId}

        and sa.attrValueId in

            #{item.attrValueId}

      group by s.id
      having count(*) = #{skuAttrValueNum}) as list

select count(distinct s.id)
from sku_list s left outer join sku_attr_value sa on (s.id = sa.skuId)
where category3Id = #{category3Id} and isSale = 1

    and tmId = #{tmId}

mapper.select:
select s.* from sku_list s, sku_sale_attr_value sa where s.id = sa.skuId and sa.saleAttrValueId in
#{item}
group by s.id having count(*) = #{saleAttrValueIdsSize}
